    d = [1768, 33563, 146445, 333969, 487738, 578980, 637955, 693164, 742218, 744600, 705313, 628727, 548461, 470359, 393602, 324436, 259003, 199596, 149824, 109817, 79050, 55132, 37086, 67544]
